import { throwError } from "rxjs";
import { environment } from 'src/environments/environment';
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc } from "firebase/firestore";
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class ProductService {
    constructor(http) {
        this.http = http;
        this.productUrl = 'api/products/products.json';
        this.fsApp = initializeApp(environment.firebase);
        this.fs = getFirestore(this.fsApp);
        this.newDoc = {
            productId: 100,
            productName: "testItem2",
            productCode: "abc-123",
            releaseDate: "2022-02-20",
            description: "this a test 2",
            price: 1234,
            starRating: 1.0,
            imageUrl: ""
        };
    }
    getProducts() {
        return this.http.get(this.productUrl);
    }
    // use pipe to console log the data
    // getProducts(): Observable<IProduct[]> {
    //     return this.http.get<IProduct[]>(this.productUrl)
    //         .pipe(tap(data => console.log('All', JSON.stringify(data))),
    //         catchError(this.handleError)
    //     );
    // }
    getFormData() {
        return this.http.get('/assets/product-form.json');
    }
    async addProduct(formEntryData) {
        try {
            // cleanup data
            const formEntryDataClean = {};
            Object.assign(formEntryDataClean, formEntryData);
            castValuesToProperType(formEntryDataClean);
            // send data to firestore
            const docRef = await addDoc(collection(this.fs, "bt-products"), formEntryDataClean);
            console.log("Document written with ID: ", docRef.id);
        }
        catch (e) {
            console.error("Error adding document: ", e);
        }
    }
    castValuesToProperType(o) {
        if (o) {
            for (let [key, value] of Object.entries(o)) {
                console.log(key);
                if (value && typeof value === 'string') {
                    o[key] = parseFloat(value.replace(/\D/g, ''));
                }
            }
        }
    }
    handleError(err) {
        //log the error
        let errorMessage = '';
        if (err.error instanceof ErrorEvent) {
            // A client-side or network error occurred
            errorMessage = `An error occurred: ${err.error.message}`;
        }
        console.log(errorMessage);
        return throwError(errorMessage);
    }
}
