import { tap, throwError } from "rxjs";
import { catchError } from "rxjs";
import { environment } from 'src/environments/environment';
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc } from "firebase/firestore";
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class ProductService {
    constructor(http) {
        this.http = http;
        this.productUrl = 'api/products/products.json';
        this.fsApp = initializeApp(environment.firebase);
        this.fs = getFirestore();
    }
    getProducts() {
        return this.http.get(this.productUrl).pipe(tap(data => console.log('All', JSON.stringify(data))), catchError(this.handleError));
    }
    async addProduct() {
        try {
            const docRef = await addDoc(collection(this.fs, "users"), {
                first: "Ada",
                last: "Lovelace",
                born: 1815
            });
            console.log("Document written with ID: ", docRef.id);
        }
        catch (e) {
            console.error("Error adding document: ", e);
        }
    }
    handleError(err) {
        //log the error
        let errorMessage = '';
        if (err.error instanceof ErrorEvent) {
            // A client-side or network error occurred
            errorMessage = `An error occurred: ${err.error.message}`;
        }
        console.log(errorMessage);
        return throwError(errorMessage);
    }
}
