import { Observable, throwError } from "rxjs";
import { environment } from 'src/environments/environment';
import { initializeApp } from "firebase/app";
import { getFirestore, collection, query } from "firebase/firestore";
import { addDoc, onSnapshot } from "firebase/firestore";
import * as i0 from "@angular/core";
export class ProductService {
    constructor() {
        this.fsApp = initializeApp(environment.firebase);
        this.fs = getFirestore(this.fsApp);
        this.productData = [];
        this.allProducts = new Observable((observer) => {
            const q = query(collection(this.fs, "bt-products"));
            onSnapshot(q, (snapshot) => {
                let products = [];
                snapshot.docs.forEach((doc) => {
                    products.push(Object.assign(Object.assign({}, doc.data()), { id: doc.id }));
                });
                this.productData = products;
                observer.next(this.productData);
            });
        });
        // subscribe to firestore collection
    }
    async addProduct(formEntryDataIn) {
        // console.log(formEntryData);
        let msg = "Error adding document: ";
        try {
            // cleanup data
            const formEntryData = {};
            Object.assign(formEntryData, formEntryDataIn);
            this.castValuesToProperType(formEntryData);
            // send data to firestore
            const docRef = await addDoc(collection(this.fs, "bt-products"), formEntryData);
            // this.getProductsAll;
            msg = "Document written with ID: ", docRef;
        }
        catch (e) {
            msg += e;
        }
        return msg;
    }
    handleError(err) {
        //log the error
        let errorMessage = '';
        if (err.error instanceof ErrorEvent) {
            // A client-side or network error occurred
            errorMessage = `An error occurred: ${err.error.message}`;
        }
        console.log(errorMessage);
        return throwError(() => new Error(errorMessage));
    }
}
